import React, {useEffect} from 'react';
import { Context } from '../context/GlobalState'
import validator from './Validator';

export const AddTransaction = () => {
    const initState = {
        text: '',
        amount: ''
    }
 const { dispatch } = React.useContext(Context)
 const [text, setText] = React.useState('');
 const [amount, setAmount] = React.useState('');
 const [err, setError] = React.useState(text, amount)

 const date = new Date().getDay();

 console.log(date)

 const onSubmitHandler = (e) => {
  e.preventDefault()


  const data = {
   id: Math.floor(Math.random() * 1000),
   text,
   amount: +amount,
  }
  setError(validator(text, amount))
  console.log(err)


  document.getElementById("reset").reset();

  
//   dispatch({ type: 'AddItem', AddTransanction: data })
 }

 useEffect(() => {
     if ( Object.keys(err).length === 0 ){
        dispatch({ type: 'AddItem', AddTransanction: data })
     }
 }, [err])
 
 return (
  <>
   <h3>Add new transaction</h3>
   <form onSubmit={onSubmitHandler} id="reset">
    <div className="form-control">
     <label htmlFor="text">Text</label>

     <input
      type="text"
      placeholder="Enter text..."
      onChange={(e) => setText(e.target.value)}
     />
     { err.text && <span className="error">{err.text}  </span> }
    </div>

    <div className="form-control">
     <label htmlFor="amount">Amount <br />
     (negative - expense, positive + income)
     </label>

     <input
      type="text"
      placeholder="Enter amount..."
      onChange={(e) => setAmount(e.target.value)}
     />
      { err.amount && <span className="error">{err.amount}  </span> }
    </div>
    <input type="submit" className="btn" value="Add Transaction" />
   </form>
  </>
 )
}
